mapper.select:
select

from t_user
where id = #{id, jdbcType = INTEGER}

mapper.update:
update t_user set
real_name = #{realname,jdbcType=VARCHAR},
password = #{password,jdbcType=VARCHAR},
user_status = #{userStatus,jdbcType=CHAR},
update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id, jdbcType = INTEGER}
